angular
  .module('nwmApp', [])
  .controller('nwmCtrl', ctrl);

ctrl.$inject = ['$http'];

function ctrl($http) {
  var vm = this;

  vm.list1 = [1, 3, 5, 7, 9];
  vm.list2 = [2, 4, 6, 8, 10];

  $http
    .jsonp('https://randomuser.me/api/', {
      params: {
        callback: 'JSON_CALLBACK',
        results: 100,
        nat: 'ca',
        inc: 'name,picture,id'
      }
    })
    .then(function(success) {
      vm.results = success.data.results;
    },
    function(error) {
      console.error(error)
    })
}
